def find_final_floor(instructions):
    floor = 0
    for char in instructions:
        if char == '(':
            floor += 1
        elif char == ')':
            floor -= 1
    return floor
final_floor = find_final_floor(instructions)
print(f"what floor: {final_floor}")

def find_first_basement_position(instructions):
    floor = 0
    position = 1
    for char in instructions:
        if char == '(':
            floor += 1
        elif char == ')':
            floor -= 1
        if floor == -1:
            return position
        position += 1
    return -1
first_basement_pos = find_first_basement_position(instructions)
print(f"position first enter the basement: {first_basement_pos}")